@mixin hover($_property, $_toValue) {
  transition: #{$_property} 0.3s;
  &:hover {
    #{$_property}: $_toValue;
  }
}

@mixin smartphone {
  @media (min-width: 640px) {
    @content;
  }
}

@mixin smalldesktop {
  @media (min-width: 1280px) {
    @content;
  }
}

@mixin desktop {
  @media (min-width: 1440px) {
    @content;
  }
}

